]==true){sign_in($(this));return false;}});$("#password-form").validate({rules:{password_email:{required:true,email:true},},errorClass:"form-invalid",});$("#password-form").submit(function(){remove_loading($(this));if(options['useAJAX']==true){password($(this));return false;}});function remove_loading($form){$form.find('[type=submit]').removeClass('error success');$form.find('.login-form-main-message').removeClass('show error success').html('');} function form_loading($form){$form.find('[type=submit]').addClass('clicked').html(options['btn-loading']);} function form_success($form,text){$form.find('[type=submit]').addClass('success').html(options['btn-success']);$form.find('.login-form-main-message').addClass('show success').html(text);} function form_failed($form,text){$form.find('[type=submit]').addClass('error').html(options['btn-error']);$form.find('.login-form-main-message').addClass('show error').html(text);} function sign_up($form){if($form.valid()){form_loading($form);setTimeout(function(){$.ajax({url:'/sign_up',method:'POST',data:{email:$form.find('input[name=reg_email]').val(),password:$form.find('input[name=reg_password]').val()},success:function(data){if(data.success){form_success($form,"Hooray! Welcome on board!");setTimeout(function(){window.location.href="/projects";},2000);}else if(!data.success){form_failed($form,"User with this email already exists!");}else{alert(data.success);}}});},2000);}} function sign_in($form){if($form.valid()){form_loading($form);setTimeout(function(){$.ajax({url:'/sign_in',method:'POST',data:{email:$form.find('input[name=log_email]').val(),password:$form.find('input[name=log_password]').val()},success:function(data){if(data.success){form_success($form,"Welcome back!");setTimeout(function(){window.location.href="/projects";},2000);}else if(!data.success){form_failed($form,"User with this email not found!");}else{alert(data.success);}}});},2000);}} function password($form){if($form.valid()){form_loading($form);setTimeout(function(){$.ajax({url:'/remind_password',method:'POST',data:{email:$form.find('input[name=password_email]').val()},success:function(data){console.log(data);if(data.success){form_success($form,"Check your email!");}else if(!data.success){form_failed($form,"User with this email not found!");}else{alert(data.success);}}});},2000);}}})(jQuery);</script> <?}?></body></html>